import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import NavBar from './NavBar3';
import { FaSearch } from 'react-icons/fa';

// Languages Data
const Languages = [
  {
    title: "Hindi",
    imgSrc: "https://d18x2uyjeekruj.cloudfront.net/wp-content/uploads/2022/10/WhatsApp-Image-2022-10-22-at-09.56.54-1024x576.jpeg",
    link: "/languages/hindi"
  },
  {
    title: "Bengali",
    imgSrc: "https://www.shutterstock.com/image-photo/ancient-inscriptions-bengali-language-texturekolkata-260nw-49957906.jpg",
    link: "/languages/bengali"
  },
  {
    title: "Telugu",
    imgSrc: "https://media.istockphoto.com/id/1405269277/vector/colorful-vector-design-made-from-telugu-letters-scripts-or-characters-telugu-is-used-in.jpg?s=612x612&w=0&k=20&c=3S6tg1xbqCF9SFojYaMRcZo3CWJKLvDAgvKyhjZ_v4A=",
    link: "/languages/telugu"
  },
  {
    title: "Marathi",
    imgSrc: "data:image/png;base64,",
    link: "/languages/marathi"
  },
  {
    title: "Tamil",
    imgSrc: "https://www.shutterstock.com/shutterstock/photos/2251460895/display_1500/stock-vector-tamil-letter-forming-the-word-tamil-vector-illustration-tamil-is-an-official-language-of-the-2251460895.jpg",
    link: "/languages/tamil"
  },
  {
    title: "Gujarati",
    imgSrc: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQC_6muayAcpqh8ZEVf8lV23ip9cS2vi7OO0A&s",
    link: "/languages/gujarati"
  },
  {
    title: "Malayalam",
    imgSrc: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTVbt0BRtBP8FwxTZw8rjlkBwiDooZn30YasA&s",
    link: "/languages/malayalam"
  },
  {
    title: "Kannada",
    imgSrc: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRLNa2BW90EgoVc_YVMos6GmVlmaX4qiV2EhQ&s",
    link: "/languages/kannada"
  },
  {
    title: "Odia",
    imgSrc: "data:image/png;base64,",
    link: "/languages/odia"
  },
  {
    title: "Punjabi",
    imgSrc: "data:image/jpeg;base64,",
    link: "/languages/punjabi"
  },
  {
    title: "Assamese",
    imgSrc: "data:image/jpeg;base64,",
    link: "/languages/assamese"
  },
  {
    title: "Maithili",
    imgSrc: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQRL9BI3b9KL6y8QkAqBOufipQ5nDF2dDtRow&s",
    link: "/languages/maithili"
  },
  // Add more languages as needed
];

const SearchBarContainer = styled.div`
  display: flex;
  justify-content: flex-end;
  margin: 20px 40px;
  position: relative;
  z-index: 1;
`;

const SearchInput = styled.input`
  padding: 10px;
  width: 300px;
  border-radius: 5px;
  border: 1px solid #ddd;
  font-size: 16px;
  outline: none;
`;

const SearchButton = styled.button`
  padding: 10px;
  border: none;
  background-color: #333;
  color: white;
  border-radius: 5px;
  margin-left: 10px;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const SearchIcon = styled(FaSearch)`
  margin-right: 5px;
`;

const LanguagesPage = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  const handleImageClick = (link) => {
    navigate(link);
  };

  const filteredLanguages = Languages.filter((language) =>
    language.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <NavBar />
      <SearchBarContainer>
        <SearchInput
          type="text"
          placeholder="Search for a language..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <SearchButton>
          <SearchIcon />
          Search
        </SearchButton>
      </SearchBarContainer>

      <Container>
        <Title>Languages of India</Title>
        <LanguagesGrid>
          {filteredLanguages.map((language, index) => (
            <LanguageCard key={index} onClick={() => handleImageClick(language.link)}>
              <Image src={language.imgSrc} alt={language.title} />
              <LanguageTitle>{language.title}</LanguageTitle>
            </LanguageCard>
          ))}
        </LanguagesGrid>
      </Container>
    </div>
  );
};

export default LanguagesPage;

// Styled Components
const Container = styled.div`
  background-color: #333;
  padding: 40px;
  text-align: center;
  min-height: 500px; /* Set minimum height to prevent shrinking */
`;

const Title = styled.h1`
  color: #e63946;
  font-size: 3rem;
  font-family: 'Dancing Script', cursive;
  text-shadow: 2px 2px #f1faee;
`;

const LanguagesGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 20px; /* Increased gap between cards */
  justify-content: center;
  min-height: 400px;
`;

const LanguageCard = styled.div`
  background-color: #e9c46a;
  border-radius: 15px;
  overflow: hidden;
  cursor: pointer;
  width: 350px; /* Fixed width */
  height: 300px; /* Fixed height */
  margin-bottom: 20px; /* Add margin at the bottom of the cards */
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.1);
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-10px);
    box-shadow: 0px 8px 20px rgba(0, 0, 0, 0.2);
  }
`;

const Image = styled.img`
  width: 100%;
  height: 200px;
  object-fit: cover; /* Ensures the image fits within the card without stretching */
`;

const LanguageTitle = styled.h2`
  font-size: 1.5rem;
  margin: 10px 0;
  color: #264653;
  font-family: 'Merriweather', serif;
`;
